function boolToNum(val)
    return "true" == val and "1" or "0"
end

function numToBool(num)
    return "0" == num and "false" or "true"
end

function convertTimeZoneToOffset(timezone)

    local offset = 0
    offset = tonumber(timezone) * 60

    return tostring(offset)
end

